import React, { Component } from 'react';
import './App.css';
import { Route,Switch,Link } from "react-router-dom";
import Login from './containers/Login';
import Home from './containers/Home';
import ListView from './containers/ListView';
import Register from './containers/Register';
import axios from 'axios';

class App extends Component {
  constructor() {
    super();
    this.state = {
        username:null
    }
    this.login = this.login.bind(this);
  };



  async login(email, password) {
    var url;
    if (process.env.NODE_ENV === 'production')
      url = 'https://www.thelocalgame.com/login';
    else
      url = 'http://localhost:3000/login';
    const response = await axios.post(url, {
      email: email,
      password: password
    }, {
      withCredentials: true
    }).catch(error => {
      if (error)
        this.setState({
          showLoginError: true
        })
    })
    if (response) {
      document.cookie = `nodebb=${response.data}`
      this.setState({
        username:email
      }, async () => {
        //after get username then add uid and data to state with another call
              var userurl;
              if (process.env.NODE_ENV === 'production')
              userurl = 'https://www.thelocalgame.com/login';
              else
              userurl = 'http://localhost/forum/api/user/fatman';
              const response =  await axios.post(userurl, {
                email: email,
                password: password
              }, {
                withCredentials: true
              }).catch(error => {
                if (error)
                  this.setState({
                    showLoginError: true
                  })
              })
              if (response) {
                console.log(response);
                this.setState({
                  uid:response
                });
              }

            });
    }


  }

render() {
  return (
    <div id='master-content'>
      <Switch>
        <Route path="/" exact render={(props) => <Home username={this.state.username} {...props}  />} />
        <Route path="/login" exact render={(props) => <Login login={this.login.bind(this)} {...props}  />} />
        <Route path="/register" exact render={(props) => <Register {...props}  />} />
        <Route path="/list" exact render={(props) => <ListView {...props}  />} />
        <Route path="/user"  />
      </Switch>
    </div>
  );
}
}
export default App;
