import React, { Component } from 'react';
import ListGroup from 'react-bootstrap/ListGroup'
import { Link } from "react-router-dom";
import axios from 'axios';
import AddList from '../components/AddList.jsx';
import { Fab, Action } from 'react-tiny-fab';
import 'react-tiny-fab/dist/styles.css';

class Home extends Component{
  constructor() {
  super();
  this.state = {
     my_checklists: [{}],
     show_add_modal: false
  }

this.handleAddListClick=this.handleAddListClick.bind(this);


};


componentDidMount() {
    this.getChecklists();
}

handleAddListClick() {
    this.setState({show_add_modal:!this.state.show_add_modal});
}

getChecklists (data, callback){

    var url;
      if(process.env.NODE_ENV === 'production')
        url='https://www.thelocalgame.com/register';
      else
        url='https://localhost:4567/api/login';

    axios.get(url,{withCredentials: true, credentials: 'include'}).then(response =>{
      //callback(response.data);
     // this.setState({my_checklists:response.data});
     console.log(response.data);
    }).catch(err =>{
      console.log(err);
    })
  }


 render(){

    return (
        <div>
            <AddList show_add_modal={this.state.show_add_modal} handleAddListClick={this.handleAddListClick.bind(this)} />
            <Fab
            mainButtonStyles={{backgroundColor: '#3498db', width: 160, borderRadius: 48}}
            style={{bottom: 0, right: 180 }}
            icon='New List'
            alwaysShowTitle={true}
            onClick={this.handleAddListClick}
            ></Fab>
            <ListGroup>
                <ListGroup.Item variant="primary">Daily Checklists</ListGroup.Item>
            {this.state.my_checklists.map((list,i) => {
                return <Link to={'/list?list_id='+this.state.my_checklists[i].list_id}>
                    <ListGroup.Item>{this.state.my_checklists[i].list_name}</ListGroup.Item>
                        </Link>
            })}
            </ListGroup>
        </div>
        )
    }
}
export default Home
